def Scrapdata(end, URL, boxtag, boxclass, nametag, nameclass, pricetag, priceclass, destag, desclass, ratingtag, raatingclass, noofrevtag, noofreviewsclass, discptag, dispclass, delivtag, delivclass,perctag,percclass):
    for i in range(1, end + 1):
        # Include the page number in the URL
        current_page_url = f"{URL}{i}"
        print(f"Scraping page {i}: {current_page_url}")  # Print the current page number
        driver.get(current_page_url)
        mainfunc(i,driver, boxtag, boxclass, nametag, nameclass, pricetag, priceclass, destag, desclass, ratingtag, raatingclass, noofrevtag, noofreviewsclass, discptag, dispclass, delivtag, delivclass,perctag,percclass)


def mainfunc(i,driver, boxtag, boxclass, nametag, nameclass, pricetag, priceclass, destag, desclass, ratingtag, raatingclass, noofrevtag, noofreviewsclass, discptag, dispclass, delivtag, delivclass,perctag,percclass):
    content = driver.page_source
    soup = BeautifulSoup(content, 'lxml')

    for a in soup.findAll(boxtag, attrs={'class': boxclass}):
        names = a.find(nametag, attrs={'class': nameclass})
        price = a.find(pricetag, attrs={'class': priceclass})
        descriptions = a.find(destag, attrs={'class': desclass})
        ratings = a.find(ratingtag, attrs={'class': raatingclass})
        noofreviews = a.find(noofrevtag, attrs={'class': noofreviewsclass})
        discounntedprices = a.find(discptag, attrs={'class': dispclass})
        deliverys = a.find(delivtag, attrs={'class': delivclass})
        discountedpercent = a.find(perctag, attrs={'class': percclass})

        # Extracting text or printing 'null' if attribute is missing
        Names.append(names.get_text() if names else 'null')
        Prices.append(price.get_text() if price else 'null')
        Descriptions.append(descriptions.get_text() if descriptions else 'null')
        Ratings.append(ratings.get_text() if ratings else 'null')
        NoOFReviews.append(noofreviews.get_text() if noofreviews else 'null')
        DiscountedPrice.append(discounntedprices.get_text() if discounntedprices else 'null')
        Delivery.append(deliverys.get_text() if deliverys else 'null')
        DiscountedPercent.append(discountedpercent.get_text() if discountedpercent else 'null')


